/*! minireset.css v0.0.3 | MIT License | github.com/jgthms/minireset.css */html,body,p,ol,ul,li,dl,dt,dd,blockquote,figure,fieldset,legend,textarea,pre,iframe,hr,h1,h2,h3,h4,h5,h6{margin:0;padding:0}h1,h2,h3,h4,h5,h6{font-size:100%;font-weight:normal}ul{list-style:none}button,input,select,textarea{margin:0}html{box-sizing:border-box}*,*:before,*:after{box-sizing:inherit}img,embed,iframe,object,audio,video{height:auto;max-width:100%}iframe{border:0}table{border-collapse:collapse;border-spacing:0}td,th{padding:0;text-align:left}

* {box-sizing:border-box;}
body	{background:#fff; font-family:sans-serif; font-size:18px; line-height:24px; color:#444; width:100%;}
a		{-webkit-transition: all .2s ease-in-out; -moz-transition: all .2s ease-in-out; -o-transition: all .2s ease-in-out; -ms-transition: all .2s ease-in-out; transition: all .2s ease-in-out;}

.content_wrap	{width:960px; margin:0 auto; position:relative;}
.data_wrap		{margin:0 auto; text-align:center;}
.upload_box 	{padding:200px 0; margin:0 auto; text-align:center; background:#fff; border:1px solid #000; max-height:429px;}
.is-dragover	{background:#fff; font-size:10em; line-height:0; padding:150px 0 250px 0;}
.is-dragover:after	{content:"\01F44D";}
.is-dragover .upload_text	{display:none;}
.upload_label	{position:relative; cursor:pointer; padding:200px 0;}
.upload_input	{width:100%; filter:alpha(opacity=0); opacity:0;}
.upload_text	{position:absolute; top:0; left:0; width:100%; padding:200px 0; font-size:22px; color:#666;}
.upload_data	{display:inline-block; width:15%; border:1px solid #fff; margin-right:15px; padding:20px 0; color:#444; text-decoration:none; text-align:center; font-size:18px;}
.upload_data:hover	{border:1px solid #444;}
.upload_data span	{display:inline-block; transform:rotate(90deg);}
.text			{padding:5em 0 1.5em 0; width:80%; margin:0 auto;}
.logo			{font-family:'Arbutus Slab',serif; font-weight:bold;}
.art			{font-family:'Arbutus Slab',serif; font-size:36px; color:#444; text-align:center; line-height:40px; margin:0 auto;}
.contact		{margin:0 auto; margin-top:2em; font-size:14px; line-height:1.5em; text-align:center;}
.contact a		{color:#034dfb; text-decoration:none; font-style:italic; font-family:monospace; background:#eee; padding:0 5px; border-radius:4px;}
.contact a:hover{color:#034dfb; text-decoration:none; background:#fff;}

/*	PRINT STYLE	*/
body.print		{background-color:#fff;}

/*	TICKER	*/
@-webkit- ticker	{0% {-webkit-transform:translate3d(0,0,0); transform:translate3d(0,0,0); visibility:visible;} 100% {-webkit-transform:translate3d(-100%,0,0);transform:translate3d(-100%,0,0);}}
@keyframes ticker	{0% {-webkit-transform:translate3d(0,0,0); transform:translate3d(0,0,0); visibility:visible;} 100% {-webkit-transform:translate3d(-100%,0,0);transform:translate3d(-100%,0,0);}}
.ticker_wrap		{display:block; position:fixed; bottom:0; width:100%; overflow:hidden; height:4rem; background-color:#000; padding-left:100%; box-sizing:content-box;}
.ticker				{display:inline-block; height:4rem; line-height:4rem; white-space:nowrap; padding-right:100%; box-sizing:content-box; -webkit-animation-iteration-count:infinite; animation-iteration-count:infinite; -webkit-animation-timing-function:linear; animation-timing-function:linear; -webkit-animation-name:ticker; animation-name:ticker; -webkit-animation-duration:20s; animation-duration:20s;}
.tick				{display:inline-block; padding:0 1rem; font-size:20px; color:#fff;}

@media screen and (max-width:600px) {
	.text			{padding:2em 0;}
	.upload_box	,
	.contact		{width:85%;}
	.upload_box,
	.upload_label	{padding:50px 0;}
	.upload_text	{padding:40px 20px;}
	.content_wrap	{width:initial;}
	br				{display:block; margin-bottom:20px;}
}
